mapper.select:
SELECT id orderId,user_id,address_id,(SELECT content FROM deliverylist WHERE TYPE in (SELECT delivery_type FROM orderlist WHERE user_id=#{0} AND TYPE=#{1})
) deliveryType,(SELECT content FROM paymentlist WHERE TYPE in (SELECT payment_type FROM orderlist WHERE user_id=#{0} AND TYPE=#{1})
) paymentType,(SELECT content FROM invoicelist WHERE TYPE in (SELECT invoice_type FROM orderlist WHERE user_id=#{0} AND TYPE=#{1})
) invoiceType,invoice_title invoiceTitle,invoice_content invoiceContent,STATUS,price,TIME,flag FROM orderlist WHERE user_id=#{0} AND TYPE=#{1}

SELECT content FROM
paymentlist WHERE
TYPE =#{type}
